commits.content:
$midnight: #2c3e50;
$clouds: #ecf0f1;

.tab {
    width: 100%;
    overflow: hidden;
    &-label {
      display: flex;
      justify-content: space-between;
      font-weight: bold;
      cursor: pointer;
      button:last-child {
        transition: all .35s;
      }
    }
    &-content {
      max-height: 0;
      padding: 0 1em;
      color: $midnight;
      background: white;
      transition: all .35s;
    }
    &-content-visible {
        padding: 1em;
        transition: all .35s;
    }
  }
